<div id="toolMediaType" class="pull-left">
    <p class="txt-dark txt-media-type">Media Type&nbsp;</p>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="facebook"><i class="ico ico-fb"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="twitter"><i class="ico ico-tw"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="youtube"><i class="ico ico-yt"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="instagram"><i class="ico ico-ig"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="tiktok"><i class="ico ico-tt"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="line"><i class="ico ico-ln"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="webboard"><i class="ico ico-wb"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="news"><i class="ico ico-nw"></i></button>
    <button type="button" class="btn btn-default btn-round btn-xs" media-type="All"><i class="ico" style="width: 0px"></i>All</button>
</div>